import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

const hobbies = [
  "making matcha latte ",
  "writing journal on my black muji notebook",
  "watching modern family and suits",
  "fast walking to classes",
  "pretending to be busy",
  "teasing my cat",
  "recurating my stationery collection",
  "simping over my celebrity crushes",
  "crafting a new kpop playlist",
  "struggling to fix the code i broke",
  "walking through coffee shops",
  "adding n items into my shopping cart",
  "scrolling through insta reels",
  "rewatching kdramas for the 3rd time",
];


function useWordCycle(words) {
  const [currentWord, setCurrentWord] = useState(
    words[Math.floor(Math.random() * words.length)]
  );

  const handleClick = () => {
    const currentIndex = words.indexOf(currentWord);
    const nextIndex = (currentIndex + 1) % words.length;
    setCurrentWord(words[nextIndex]);
  };

  return [currentWord, handleClick];
}

function Home() {
  useEffect(() => {
    document.title = "kdothings (≧∇≦)";
  }, []);

  const [currentHobby, handleHobbyClick] = useWordCycle(hobbies);

  return (
    <div>
      <h2>hi! i'm nic!</h2>
      <p>
        welcome to my little space!</p>
      <p>
        born and raised in hanoi, vietnam, i'm currently a student at <a href = "https://www.unc.edu/" class="clickable">UNC-Chapel Hill</a>. when i'm not trying to be a pioneer or an academic weapon, you can catch me learning about
        human-computer interactions, taking 35mm films, and{" "}
        <a class="clickable" id="hobbies" onClick={handleHobbyClick}>
          {currentHobby}
        </a>
        .
      </p>
      <p>
        feel free to reach out anytime at{" "}
        <a href="mailto:nicholasdo.work@gmail.com" class="clickable">
          nicholasdo.work@gmail.com
        </a>
        ,
        <br />
        or ask{" "}
        <NavLink to="/quinn-ai" class="clickable">
          <u>my ai</u>
        </NavLink>{" "}
        a question!
      </p>
      <div class="box">
        i'm looking for 2024 new grad swe positions! shoot me an{" "}
        <a href="mailto:quinnha.hello@gmail.com" class="clickable">
          email
        </a>{" "}
        if you (or your friends) are hiring &lt;3{" "}
      </div>
    </div>
  );
}

export default Home;
