import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

const majors = [
  "computer science",
  "mathematics",
  "economics",
  "business",
  "statistics & analytics",
  "data science",
  "public policy",
  "digital media",
];

function useWordCycle(words) {
  const [currentWord, setCurrentWord] = useState(
    words[Math.floor(Math.random() * words.length)]
  );

  const handleClick = () => {
    const currentIndex = words.indexOf(currentWord);
    const nextIndex = (currentIndex + 1) % words.length;
    setCurrentWord(words[nextIndex]);
  };

  return [currentWord, handleClick];
}

function About() {
  useEffect(() => {
    document.title = "kdothings";
  }, []);

  const [mymajors, handleMajorClick] = useWordCycle(majors);


  return (
    <div>
      <img src="/images/me.png" alt="my picture :>" width="150" height="auto" style={{ borderRadius: '50%' }}/>
      <br/>
      <br/>
      <p>
        here are some facts about me" i like travelling, reading, and learning new (random) things. 
        <br />
        <br />
        i am a bit here and there, so you might see me learning a new buldak recipe while trying to figure out operating systems at the same time ;-; 
        nonetheless, i welcome any learning opportunity out there and i will definitely try my best ʚ✩ɞ.
        <br />
        <br />
        while i'm still trying to figure out my passions, i like to try new things and push
        myself out of my comfort zone. i like to learn from the people around me so please connect 
      </p>
      <br/>
      <br/>
      <p>
        <h2>currently</h2>
      </p>
      <p>
        i'm a sophomore at{" "}
        <a href="https://www.unc.edu/" target="_blank" class="clickable">
          UNC-Chapel Hill
        </a>
        , working on my{" "}
        <a class="clickable" id="majors" onClick={handleMajorClick}>
          {mymajors}
        </a> degree!
      </p>
      <br />
      <br/>
      <p>
        <h2>i also dabbled in</h2>
      </p>
      <ul>
        <li>
          created websites for non-profits (
          <a
            href="https://www.developforgood.org/?gad_source=1&gclid=CjwKCAjw17qvBhBrEiwA1rU9w1hYghMW1CtS04zqAlNYKxDJpogjQdA3uJmktljnX9y-lx26vqhkKhoC5JwQAvD_BwE"
            class="clickable"
            target="_blank"
          >
            Develop for Good
          </a>
          )
        </li>
        <li>
          NLP & digital humanities research
        </li>
        <li>
          develop games (
          <a
            href="https://fptplay.vn/"
            class="clickable"
            target="_blank"
          >
            FPT Play
          </a>
          )
        </li>
        <li>
          create website{" "}
          <a
            href="https://hacknc.com/"
            target="_blank"
            class="clickable"
          >
            HackNC
          </a>
        </li>  
      </ul>
    </div>
  );
}

export default About;
