                <NavLink to="/projects">projects</NavLink> <br />
                <NavLink to="/journal">journal</NavLink> <br />
                <NavLink to="/resume">resume</NavLink> <br />
                <CSSTransition
                    in={visible}
                    appear={true}
                    timeout={500}
                    classNames="fadeIn"
                >
                    <a onClick={handleThemeChange} className="fadeIn">
                        {theme}
                    </a>
                </CSSTransition>
            </nav> */}

            <nav className="navbar">
                <Link className="nav-item" to="/">home</Link>
                <Link className="nav-item" to="/about">about</Link>
                <Link className="nav-item" to="/projects">projects</Link>
                <Link className="nav-item" to="/journal">journal</Link>
                <Link className="nav-item" to="/resume">resume</Link>
                <Link className="nav-item" to="/sun">sun</Link>
                </nav>
        </div>
    )
}

export default NavBar
